
public class SecondMethodType {
	static void degign1(char ch1,char ch2,char ch3){
		for(int i=1;i<=20;i++) {
			System.out.print(ch1);
		}
		System.out.println();
		for(int i=1;i<=20;i++) {
			System.out.print(ch2);
		}
		System.out.println();
		for(int i=1;i<=20;i++) {
			System.out.print(ch3);
		}
		System.out.println();
		
	}

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		degign1('*','-','.');
		
		System.out.println("Hello");
		degign1('$','.','@');
		
		System.out.println("Welcome");
		degign1('%','!','*');
		
		System.out.println("Java");
		degign1('*','-','.');
		
		System.out.println("Avanthi");
		degign1('.','.','.');
		
		System.out.println("Final Year");

		degign1('*','*','*');
		
	}

}
